    /** @var TPProxyClient $tpClient */
    private $tpClient;
    /** @var TPRequest $tpRequest */
    private $tpRequest;

    /**
     * TopUpSpecialAmount constructor.
     * @param TPProxyClient $client
     * @param TPRequest $request
     */
    public function __construct(TPProxyClient $client, TPRequest $request)
    {
        $this->tpClient = $client;
        $this->tpRequest = $request;
    }

    public function get()
    {
        $this->tpRequest->setUrl($this->makeUrl())->setMethod(TPRequest::METHOD_GET);

        try {
            $special_amount = $this->tpClient->call($this->tpRequest);
            if ($special_amount->status != self::SUCCESSFUL_RESPONSE) throw new Exception();
            return $special_amount->data;
        } catch (Throwable $e) {
            app('sentry')->captureException($e);
            return json_decode(self::DEFAULT_SPECIAL_AMOUNT_DATA);
        }
    }

    private function makeUrl()
    {
        $ssl_client_id = config('topup.ssl.client_id');
        return config('sheba.topup_special_amount_get_url_by_ssl') . "?client_id=$ssl_client_id&lang=en";
    }
